            return [null,{"topic": "Loading data", "payload": "User needs to be selected first"}];;
        }

        msg.topic = "SELECT * FROM gps WHERE deviceid='"+user+"' AND time >= " + fromdate + " AND time <= " + enddate + " ORDER BY time";
        msg.wifi = "SELECT * FROM wifi WHERE deviceid='"+user+"' AND time >= " + fromdate + " AND time <= " + enddate + " ORDER BY time";
